import { Component } from '@angular/core';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent {

  constructor() { }
  infoCards = [
    {
      title: 'Live Jobs',
      value: '24',
      icon: 'assets/icons/live-job-icon.png', 
    },
    {
      title: 'Applications Received',
      value: '220',
      icon: 'assets/icons/applications-icon.png',
    },
    {
      title: 'Shortlist in Progress',
      value: '80',
      icon: 'assets/icons/shortlist-icon.png',
    },
    {
      title: 'Offers on the Table',
      value: '02',
      icon: 'assets/icons/offers-icon.png',
    },
  ];


  jobList = [
    {
      title: 'Junior Data Analyst',
      datePosted: '3/5/2025',
      status: 'Open',
      statusClass: 'status-open',
      expiry: '3/5/2025',
      rounds: 3,
      applied: 23,
      candidates: 0
    },
    {
      title: 'Customer Success Executive',
      datePosted: '22/4/2025',
      status: 'Open',
      statusClass: 'status-open',
      expiry: '22/4/2025',
      rounds: 0,
      applied: 50,
      candidates: 0
    },
    {
      title: 'Community Engagement Lead',
      datePosted: '20/4/2025',
      status: 'Paused',
      statusClass: 'status-paused',
      expiry: '20/4/2025',
      rounds: 1,
      applied: 101,
      candidates: 0
    },
    {
      title: 'Full-Stack Developer',
      datePosted: '20/4/2025',
      status: 'Closed',
      statusClass: 'status-closed',
      expiry: '20/4/2025',
      rounds: 0,
      applied: 56,
      candidates: 0
    },
    {
      title: 'HR Business Partner',
      datePosted: '12/3/2025',
      status: 'Expired',
      statusClass: 'status-expired',
      expiry: '12/3/2025',
      rounds: 4,
      applied: 34,
      candidates: 0
    }
  ];

  dropdownOpenIndex: number | null = null;

statusOptions = [
  { label: 'Open', class: 'status-open' },
  { label: 'Pause', class: 'status-paused' },
  { label: 'Close', class: 'status-closed' }
];

toggleDropdown(index: number): void {
  this.dropdownOpenIndex = this.dropdownOpenIndex === index ? null : index;
}

changeStatus(index: number, option: any): void {
  this.jobList[index].status = option.label;
  this.jobList[index].statusClass = option.class;
  this.dropdownOpenIndex = null;
}
sortDropdownOpen = false;

sortOptions: string[] = [
  'Newest First',
  'Oldest First',
  'Open Jobs',
  'Paused Jobs',
  'Closed Jobs',
  'Expired Jobs'
];

selectedSortOption: string = 'Newest First';

toggleSortDropdown() {
  this.sortDropdownOpen = !this.sortDropdownOpen;
}

selectSortOption(option: string) {
  this.selectedSortOption = option;
  this.sortDropdownOpen = false;
  // TODO: Add sorting/filtering logic here
}

closeSortDropdown() {
  this.sortDropdownOpen = false;
}
}
