import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-splash-screen',
  templateUrl: './splash-screen.component.html',
  styleUrls: ['./splash-screen.component.css']  // or .scss if you’re using SCSS
})
export class SplashScreenComponent implements OnInit {

  constructor(private router: Router) {}

  ngOnInit(): void {
    // setTimeout(() => {
    //   this.router.navigate(['/login']);
    // }, 2500); // 2.5 seconds
  }
}
<div class="splash-container">
  <div class="splash-card">
    <img src="assets/logo.png" alt="Skillmuni Logo" class="splash-logo" />

    <div class="welcome-text">
      <h3>Welcome to your hiring dashboard</h3>
      <p>
        — let’s kick off your
        <span class="highlight">recruitment journey.</span>
        <span class="arrow">→</span>
      </p>
    </div>
  </div>

  <p class="splash-footer">©TGC Technologies Pvt Ltd</p>
</div>
