import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-company-profile',
  templateUrl: './company-profile.component.html',
  styleUrls: ['./company-profile.component.css']
})
export class CompanyProfileComponent implements OnInit {
  profileForm!: FormGroup;
  countryList = ["India", "United States", "United Kingdom", "Australia", "Canada"];
  cityList = ["Mumbai", "Delhi", "Bangalore", "New York", "London", "Sydney"];
  industryList = ["Technology", "Finance", "Healthcare", "Education", "Retail"];
  sizeList = ["1-10", "11-50", "51-200", "201-500", "501-1000", "1000+"];
  logoPreview: string | ArrayBuffer | null = null;

  constructor(private fb: FormBuilder) {}

  ngOnInit() {
    this.profileForm = this.fb.group({
      companyName: ['', Validators.required],
      companyEmail: [{ value: '', disabled: true }],
      companyWebsite: ['', Validators.required],
      companyLogo: [null],
      country: ['', Validators.required],
      city: ['', Validators.required],
      industry: ['', Validators.required],
      size: ['', Validators.required],
      contactName: ['', Validators.required],
      contactEmail: ['', [Validators.required, Validators.email]],
      about: ['', [Validators.required, Validators.maxLength(100)]]
    });
  }

  onLogoChange(event: any) {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = e => this.logoPreview = reader.result;
      reader.readAsDataURL(file);
      this.profileForm.patchValue({ companyLogo: file });
    }
  }

  onSubmit() {
    if (this.profileForm.valid) {
      // Placeholder: send data to backend service
      alert('Profile submitted!');
    } else {
      this.profileForm.markAllAsTouched();
    }
  }
}
<app-header></app-header>
<div class="profile-wrapper">
  <div class="profile-card">
    <h2 class="profile-title">Company Profile</h2>
    <p class="profile-subtitle">Complete it to Proceed! Takes 5 mins tops!</p>
    <form [formGroup]="profileForm" (ngSubmit)="onSubmit()">
      <div class="profile-grid">
        <div class="profile-field">
          <label>1. Company <span class="highlight">Name</span></label>
          <input formControlName="companyName" placeholder="Type in your Company Name here...." />
        </div>
        <div class="profile-field">
          <label>2. Company <span class="highlight">Email</span></label>
          <input formControlName="companyEmail" placeholder="Written by Default (Same as Sign Up)" [attr.disabled]="true" />
        </div>
        <div class="profile-field">
          <label>3. Company Website/LinkedIn Link</label>
          <input formControlName="companyWebsite" placeholder="Drop your website or LinkedIn link here...." />
        </div>
        <div class="profile-field">
          <label>4. Company Logo</label>
          <div class="logo-upload">
            <input type="file" (change)="onLogoChange($event)" accept="image/*" />
            <label class="upload-btn" for="logoInput">
              Click here to upload your company logo
              <img src="assets/icons/upload-icon.svg" alt="Upload" />
            </label>
            <input id="logoInput" type="file" (change)="onLogoChange($event)" accept="image/*" />
                        <span *ngIf="logoPreview" class="logo-preview"><img [src]="logoPreview" alt="Logo Preview" /></span>
          </div>
        </div>
        <div class="profile-field">
          <label>5. Head Office <span class="highlight">Country</span></label>
          <select formControlName="country">
            <option value="" disabled>Choose Country from the drop-down</option>
            <option *ngFor="let c of countryList" [value]="c">{{c}}</option>
          </select>
        </div>
        <div class="profile-field">
          <label>6. Head Office <span class="highlight">City</span></label>
          <select formControlName="city">
            <option value="" disabled>Choose City from the drop-down</option>
            <option *ngFor="let c of cityList" [value]="c">{{c}}</option>
          </select>
        </div>
        <div class="profile-field">
          <label>7. Company's <span class="highlight">Industry</span></label>
          <select formControlName="industry">
            <option value="" disabled>Choose Industry from the drop-down</option>
            <option *ngFor="let i of industryList" [value]="i">{{i}}</option>
          </select>
        </div>
        <div class="profile-field">
          <label>8. Company <span class="highlight">Size</span></label>
          <input formControlName="size" placeholder="How big is your team? e.g. 11-50 people" />
        </div>
        <div class="profile-field">
          <label>9. Contact Person <span class="highlight">Name</span></label>
          <input formControlName="contactName" placeholder="Type in Full name of your point of contact...." />
        </div>
        <div class="profile-field">
          <label>10. Contact Person <span class="highlight">Email/No</span></label>
          <input formControlName="contactEmail" placeholder="Type in the best email or number to reach you...." />
        </div>
      </div>
      <div class="profile-about">
        <label>11. About the <span class="highlight">Company</span></label>
        <textarea formControlName="about" rows="3" maxlength="100" placeholder="Tell us about your company..."></textarea>
        <span class="about-limit">Upto 100 words</span>
      </div>
      <button class="profile-submit" type="submit">Let's Go <span class="arrow">→</span></button>
    </form>
  </div>
</div>
