import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';

interface DashboardCard {
  title: string;
  count: number;
  trend: number;
  trendType: 'up' | 'down';
  trendText: string;
}

interface ActivityItem {
  id: number;
  icon: string;
  description: string;
  time: string;
}

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {
  cards: DashboardCard[] = [
    {
      title: 'Applications',
      count: 12,
      trend: 2,
      trendType: 'up',
      trendText: 'this week'
    },
    {
      title: 'Interviews',
      count: 3,
      trend: 1,
      trendType: 'up',
      trendText: 'this week'
    },
    {
      title: 'Saved Jobs',
      count: 8,
      trend: 1,
      trendType: 'down',
      trendText: 'this week'
    }
  ];

  activities: ActivityItem[] = [
    {
      id: 1,
      icon: 'üìÖ',
      description: 'Interview scheduled with Tech Corp',
      time: '2 hours ago'
    },
    {
      id: 2,
      icon: 'üìù',
      description: 'Application submitted to Design Studio',
      time: '1 day ago'
    },
    {
      id: 3,
      icon: 'üëÄ',
      description: 'Your profile was viewed by 5 employers',
      time: '2 days ago'
    }
  ];

  userName: string = 'John';

  constructor(private router: Router) { }

  ngOnInit(): void {
    // Initialize component
  }

  navigateTo(route: string): void {
    this.router.navigate([`/${route}`]);
  }
}
