import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { ForgotPasswordService } from '../services/forgot-password.service';

@Component({
  selector: 'app-forgot-password',
  templateUrl: './forgot-password.component.html',
  styleUrls: ['./forgot-password.component.css']
})
export class ForgotPasswordComponent {
  email: string = '';
  showError: boolean = false;
  isLoading: boolean = false;
  errorMessage: string = '';

  constructor(
    private router: Router,
    private forgotPasswordService: ForgotPasswordService
  ) {}

  onSubmit() {
    this.showError = true;

    if (!this.email) {
      this.errorMessage = 'Please enter your email address';
      return;
    }

    if (!this.isValidEmail(this.email)) {
      this.errorMessage = 'Please enter a valid email address';
      return;
    }

    this.isLoading = true;
    this.errorMessage = '';

    // Simulate API call
    setTimeout(() => {
      try {
        // In a real app, you would call your auth service here
        // For now, we'll just simulate a successful response
        this.forgotPasswordService.setEmail(this.email);
        this.forgotPasswordService.setToken('simulated-reset-token');
        this.router.navigate(['/forgot-password/email-sent']);
      } catch (error) {
        this.errorMessage = 'An error occurred. Please try again.';
        console.error('Forgot password error:', error);
      } finally {
        this.isLoading = false;
      }
    }, 1000);
  }

  navigateToLogin() {
    this.router.navigate(['/login']);
  }

  private isValidEmail(email: string): boolean {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  }
}
<div class="login-container">
  <div class="login-card">
    <img src="assets/logos/skillmuni-login-logo.png" alt="Skillmuni Logo" class="login-logo" />

    <h2 class="login-title">Forgot Password?</h2>
    <p class="login-subtitle">
      We'll send a reset link to your registered email below — click it to set a new password and you're good to go.
    </p>

    <form #forgotForm="ngForm" (ngSubmit)="onSubmit()" *ngIf="!isLoading">
      <div class="form-group">
        <div class="label-row">
          <label>Company Email:</label>
          <span *ngIf="showError && !email" class="error-text">Please enter this ❌</span>
        </div>
        <input 
          type="email" 
          [(ngModel)]="email" 
          name="email" 
          class="form-input" 
          [class.error]="showError && errorMessage"
          placeholder="Type in your Company Email here...."
          (input)="errorMessage = ''"
          required>
      </div>

      <div *ngIf="errorMessage" class="error-message">
        {{ errorMessage }}
      </div>

      <button type="submit" class="login-btn" [disabled]="isLoading">
        Send Reset Link <span class="submit-icon">&#10132;</span>
      </button>
    </form>

    <div *ngIf="isLoading" class="loading-container">
      <div class="spinner"></div>
      <p>Sending reset link...</p>
    </div>

    <div class="login-footer">
      <hr />
      <p>
        Remember your password?
        <button class="signup-inline-btn" (click)="navigateToLogin()" [disabled]="isLoading">Log In</button>
      </p>
    </div>
  </div>
</div>
