import { Component } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {
  orgLogoUrl = 'assets/logo.svg';
  userProfileImg = 'assets/profile.svg';
  activeLink: 'home' | 'leaderboard' | 'dashboard' = 'home';
  notifications: string[] = ['Welcome to SkillMuni!', 'You have a new message.'];
  showNotifications = false;

  setActiveLink(link: 'home' | 'leaderboard' | 'dashboard') {
    this.activeLink = link;
  }

  toggleNotifications() {
    this.showNotifications = !this.showNotifications;
  }
}

<nav class="navbar navbar-expand-lg navbar-light bg-light shadow-nav">
  <div class="container-fluid">
    <!-- Left Section: Logo -->
    <div class="left-section d-flex align-items-center">
      <img src="assets/logo.svg" alt="SkillMuni Logo" class="logo-left" />
      <span class="brand-text">SKILLMUNI<sup>™</sup></span>
    </div>
    <!-- Right Section: Notification & Profile -->
    <div class="right-section d-flex align-items-center">
      <button class="header-icon-btn bell-btn" aria-label="Notifications">
        <img src="assets/icons/bell-icon.png" alt="Notifications" class="bell-icon" />
      </button>
      <button class="header-icon-btn profile-btn" aria-label="Profile">
        <img src="assets/icons/profile-icon.png" alt="Profile" class="profile-icon" />
      </button>
    </div>
  </div>
</nav>
