import { Component, OnInit, HostBinding } from '@angular/core';
import { Router } from '@angular/router';
import { trigger, transition, style, animate, query, stagger } from '@angular/animations';

export const splashAnimation = [
  trigger('splashAnimation', [
    transition(':leave', [
      query('.splash-card', [
        animate('400ms ease-in-out', style({ 
          transform: 'translateY(-40px)',
          opacity: 0 
        }))
      ]),
      query('.splash-logo', [
        animate('400ms 100ms ease-in-out', style({ 
          transform: 'scale(0.9) translateY(-30px)',
          opacity: 0 
        }))
      ]),
      query('.welcome-text h3', [
        animate('400ms 150ms ease-in-out', style({ 
          transform: 'translateY(-20px)',
          opacity: 0 
        }))
      ]),
      query('.welcome-text p', [
        animate('400ms 200ms ease-in-out', style({ 
          transform: 'translateY(-20px)',
          opacity: 0 
        }))
      ])
    ])
  ])
];

@Component({
  selector: 'app-splash-screen',
  templateUrl: './splash-screen.component.html',
  styleUrls: ['./splash-screen.component.css'],
  animations: [splashAnimation]
})
export class SplashScreenComponent implements OnInit {
  @HostBinding('@splashAnimation') animate = true;
  isExiting = false;

  constructor(private router: Router) {}

  ngOnInit(): void {
    setTimeout(() => {
      this.isExiting = true;
      // Give time for the exit animation to complete
      setTimeout(() => {
        this.router.navigate(['/sign-up']);
      }, 300);
    }, 2500); // 2.5 seconds
  }
}
<div class="splash-container" [@fadeOut]="isExiting">
  <div class="splash-card">
    <img src="assets/logos/skillmuni-login-logo.png" alt="Skillmuni Logo" class="splash-logo" />

    <div class="welcome-text">
      <h3>Welcome to your hiring dashboard</h3>
      <p>
        — let’s kick off your
        <span class="highlight-container">
          <span class="highlight">recruitment journey.</span>
          <img src="assets/icons/long-arrow.png" alt="arrow" class="arrow-image" />
        </span>
      </p>
    </div>
  </div>
</div>
