import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-splash-screen',
  templateUrl: './splash-screen.component.html',
  styleUrls: ['./splash-screen.component.css']  // or .scss if you’re using SCSS
})
export class SplashScreenComponent implements OnInit {

  constructor(private router: Router) {}

  ngOnInit(): void {
    // setTimeout(() => {
    //   this.router.navigate(['/login']);
    // }, 2500); // 2.5 seconds
  }
}
<div class="splash-wrapper">
  <div class="splash-card">
    <div class="logo-container">
      <img src="assets/logo.png" alt="Skillmuni Logo" class="logo" />
    </div>
    <h2 class="title">Welcome to your hiring dashboard</h2>
    <p class="subtitle">
      — let’s kick off your <span class="highlight">recruitment journey.</span>
      <span class="arrow">→</span>
    </p>
  </div>
</div>
