import { Component, OnInit, ViewChild } from '@angular/core';
import { Router } from '@angular/router';
import { NgForm } from '@angular/forms';
import { ForgotPasswordService } from '../services/forgot-password.service';

@Component({
  selector: 'app-forgot-password',
  templateUrl: './forgot-password.component.html',
  styleUrls: ['./forgot-password.component.css']
})
export class ForgotPasswordComponent implements OnInit {
  @ViewChild('forgotForm') forgotForm!: NgForm;

  email: string = '';
  isLoading: boolean = false;
  showError: boolean = false;
  errorMessage: string = '';
  isEmailSent: boolean = false;

  constructor(
    private router: Router,
    private forgotPasswordService: ForgotPasswordService
  ) { }

  ngOnInit(): void {}

  onInputChange(): void {
    this.errorMessage = '';
  }

  resendEmail(event: Event): void {
    event.preventDefault();
    if (this.email) {
      this.onSubmit();
    }
  }

  onSubmit(): void {
    this.showError = true;

    if (!this.email) {
      this.errorMessage = 'Please enter your email address';
      return;
    }

    // Basic email validation
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(this.email)) {
      this.errorMessage = 'Please enter a valid email address';
      return;
    }

    this.isLoading = true;
    this.errorMessage = '';

    // Simulate API call
    setTimeout(() => {
      try {
        // In a real app, you would call your auth service here
        // For now, we'll just simulate a successful response
        this.forgotPasswordService.setEmail(this.email);
        this.forgotPasswordService.setToken('simulated-reset-token');
        this.router.navigate(['/forgot-password/email-sent']);
      } catch (error) {
        this.errorMessage = 'An error occurred. Please try again.';
        console.error('Forgot password error:', error);
      } finally {
        this.isLoading = false;
        this.isEmailSent = true;
      }
    }, 1500);
  }

  navigateToLogin(event?: Event): void {
    if (event) {
      event.preventDefault();
    }
    this.router.navigate(['/login']);
  }
  }
}
<div class="forgot-password-container">
  <div class="forgot-password-card">
    <div class="logo-container">
      <img src="assets/logos/skillmuni-login-logo.png" alt="SkillMuni Logo" class="logo" />
    </div>

    <h1 class="title">Forgot Password?</h1>
    <p class="subtitle">
      We'll send a reset link to your registered email below — click it to set a new password and you're good to go.
    </p>

    <form #forgotForm="ngForm" (ngSubmit)="onSubmit()" class="forgot-form" *ngIf="!isLoading && !isEmailSent">
      <div class="form-group">
        <label for="email">Company Email</label>
        <div class="input-container">
          <input 
            type="email" 
            id="email"
            [(ngModel)]="email" 
            name="email" 
            class="form-control"
            [class.error]="showError && (!email || errorMessage)"
            placeholder="Type in your Company Email here..."
            (input)="onInputChange()"
            required>
        </div>
        <div *ngIf="showError && !email" class="error-message">
          Email is required
        </div>
        <div *ngIf="errorMessage" class="error-message">
          {{ errorMessage }}
        </div>
      </div>

      <button type="submit" class="submit-btn" [disabled]="isLoading">
        <span *ngIf="!isLoading">Send Reset Link</span>
        <span *ngIf="isLoading" class="spinner"></span>
      </button>
    </form>

    <div *ngIf="isLoading" class="loading-state">
      <div class="spinner"></div>
      <p>Sending reset link...</p>
    </div>

    <div *ngIf="isEmailSent" class="success-message">
      <div class="success-icon">✓</div>
      <h3>Check Your Email</h3>
      <p>We've sent a password reset link to your email address.</p>
      <p class="email-sent">{{ email }}</p>
      <button class="back-to-login" (click)="navigateToLogin()">Back to Login</button>

      <div class="resend-container">
        <p>Didn't receive the email? <a href="#" (click)="resendEmail($event)">Resend</a></p>
      </div>
    </div>

    <div class="footer" *ngIf="!isEmailSent">
      <p>Remember your password? <a href="#" (click)="navigateToLogin($event)">Login</a></p>
    </div>
  </div>
</div>
