import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { ForgotPasswordService } from '../services/forgot-password.service';

@Component({
  selector: 'app-forgot-password',
  templateUrl: './forgot-password.component.html',
  styleUrls: ['./forgot-password.component.css']
})
export class ForgotPasswordComponent {
  email: string = '';
  showError: boolean = false;
  isLoading: boolean = false;
  errorMessage: string = '';

  constructor(
    private router: Router,
    private forgotPasswordService: ForgotPasswordService
  ) {}

  onSubmit() {
    this.showError = true;

    if (!this.email) {
      this.errorMessage = 'Please enter your email address';
      return;
    }

    if (!this.isValidEmail(this.email)) {
      this.errorMessage = 'Please enter a valid email address';
      return;
    }

    this.isLoading = true;
    this.errorMessage = '';

    // Simulate API call
    setTimeout(() => {
      try {
        // In a real app, you would call your auth service here
        // For now, we'll just simulate a successful response
        this.forgotPasswordService.setEmail(this.email);
        this.forgotPasswordService.setToken('simulated-reset-token');
        this.router.navigate(['/forgot-password/email-sent']);
      } catch (error) {
        this.errorMessage = 'An error occurred. Please try again.';
        console.error('Forgot password error:', error);
      } finally {
        this.isLoading = false;
      }
    }, 1000);
  }

  navigateToLogin() {
    this.router.navigate(['/login']);
  }

  private isValidEmail(email: string): boolean {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  }
}
<div class="forgot-password-container">
  <div class="forgot-password-card">
    <div class="logo-container">
      <img src="assets/logos/skillmuni-login-logo.png" alt="Skillmuni Logo" class="logo" />
      <h1 class="company-name">SkillMuni</h1>
    </div>

    <div class="form-container">
      <h2 class="title">Forgot Password?</h2>
      <p class="subtitle">
        We'll send a reset link to your registered email below — click it to set a new password and you're good to go.
      </p>

      <form #forgotForm="ngForm" (ngSubmit)="onSubmit()" class="forgot-form">
        <div class="form-group">
          <label for="email" class="form-label">Company Email</label>
          <div class="input-container">
            <input 
              id="email"
              type="email" 
              [(ngModel)]="email" 
              name="email" 
              class="form-input" 
              [class.error]="showError && errorMessage"
              placeholder="Type in your Company Email here...."
              (input)="errorMessage = ''"
              required>
            <span *ngIf="showError && !email" class="error-icon">❌</span>
          </div>
          <div *ngIf="showError && !email" class="error-message">
            Please enter this
          </div>
        </div>

        <button type="submit" class="submit-btn" [disabled]="isLoading">
          <span *ngIf="!isLoading">Send Reset Link</span>
          <span *ngIf="isLoading">Sending...</span>
          <span class="arrow-icon">→</span>
        </button>
      </form>
    </div>
  </div>
</div>
