import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';
import { Router, ActivatedRoute } from '@angular/router';
import { ForgotPasswordService } from '../../services/forgot-password.service';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-forgot-password-create',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule],
  templateUrl: './forgot-password-create.component.html',
  styleUrls: ['./forgot-password-create.component.css']
})
export class ForgotPasswordCreateComponent implements OnInit {
  passwordForm: FormGroup;
  showPassword = false;
  showConfirmPassword = false;
  isLoading = false;
  errorMessage = '';
  email = '';
  token = '';

  constructor(
    private fb: FormBuilder,
    private router: Router,
    private route: ActivatedRoute,
    private forgotPasswordService: ForgotPasswordService
  ) {
    this.passwordForm = this.fb.group({
      password: ['', [
        Validators.required,
        Validators.minLength(8),
        Validators.pattern('^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$')
      ]],
      confirmPassword: ['', Validators.required]
    }, { validator: this.passwordMatchValidator });
  }

  ngOnInit() {
    // Get email and token from the service or route params
    this.forgotPasswordService.email$.subscribe(email => this.email = email || '');
    this.forgotPasswordService.token$.subscribe(token => this.token = token || '');

    // In a real app, you might want to validate the token here
    if (!this.token) {
      this.router.navigate(['/forgot-password']);
    }
  }

  passwordMatchValidator(g: FormGroup) {
    return g.get('password')?.value === g.get('confirmPassword')?.value
      ? null : { 'mismatch': true };
  }

  togglePasswordVisibility(field: 'password' | 'confirmPassword') {
    if (field === 'password') {
      this.showPassword = !this.showPassword;
    } else {
      this.showConfirmPassword = !this.showConfirmPassword;
    }
  }

  get password() { return this.passwordForm.get('password'); }
  get confirmPassword() { return this.passwordForm.get('confirmPassword'); }

  getPasswordErrors() {
    if (!this.password?.errors) return '';

    if (this.password.errors['required']) return 'Password is required';
    if (this.password.errors['minlength']) return 'Password must be at least 8 characters';
    if (this.password.errors['pattern']) {
      return 'Password must include at least one uppercase letter, one lowercase letter, one number, and one special character';
    }
    return '';
  }

  onSubmit() {
    if (this.passwordForm.invalid) {
      this.passwordForm.markAllAsTouched();
      return;
    }

    this.isLoading = true;
    this.errorMessage = '';

    // Simulate API call
    setTimeout(() => {
      try {
        // In a real app, you would call your auth service here to reset the password
        // For example:
        // await this.authService.resetPassword(this.email, this.token, this.passwordForm.value.password);
        this.router.navigate(['/forgot-password/success']);
      } catch (error) {
        this.errorMessage = 'Failed to reset password. Please try again.';
        console.error('Reset password error:', error);
      } finally {
        this.isLoading = false;
      }
    }, 1000);
  }

  navigateBack() {
    this.router.navigate(['/forgot-password']);
  }
}
<div class="login-container">
  <div class="login-card">
    <div class="back-button" (click)="navigateBack()">
      <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M15 18L9 12L15 6" stroke="#34437A" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
      </svg>
      Back
    </div>

    <img src="assets/logos/skillmuni-login-logo.png" alt="Skillmuni Logo" class="login-logo" />

    <h2 class="login-title">Create New Password</h2>
    <p class="login-subtitle">
      Your new password must be different from previous used passwords.
    </p>

    <form [formGroup]="passwordForm" (ngSubmit)="onSubmit()" *ngIf="!isLoading">
      <div class="form-group">
        <div class="label-row">
          <label>New Password</label>
          <span *ngIf="password?.touched && password?.invalid" class="error-text">
            {{ getPasswordErrors() }}
          </span>
        </div>
        <div class="password-input-container">
          <input 
            [type]="showPassword ? 'text' : 'password'" 
            formControlName="password" 
            class="form-input" 
            placeholder="Enter new password"
            [class.error]="password?.touched && password?.invalid">
          <button type="button" class="toggle-password" (click)="togglePasswordVisibility('password')">
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M1 12C1 12 5 4 12 4C19 4 23 12 23 12C23 12 19 20 12 20C5 20 1 12 1 12Z" stroke="#34437A" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
              <path d="M12 15C13.6569 15 15 13.6569 15 12C15 10.3431 13.6569 9 12 9C10.3431 9 9 10.3431 9 12C9 13.6569 10.3431 15 12 15Z" stroke="#34437A" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
            </svg>
          </button>
        </div>
      </div>

      <div class="form-group">
        <div class="label-row">
          <label>Confirm New Password</label>
          <span *ngIf="confirmPassword?.touched && (confirmPassword?.invalid || passwordForm.hasError('mismatch'))" class="error-text">
            {{ confirmPassword?.hasError('required') ? 'Please confirm your password' : 'Passwords do not match' }}
          </span>
        </div>
        <div class="password-input-container">
          <input 
            [type]="showConfirmPassword ? 'text' : 'password'" 
            formControlName="confirmPassword" 
            class="form-input" 
            placeholder="Confirm new password"
            [class.error]="confirmPassword?.touched && (confirmPassword?.invalid || passwordForm.hasError('mismatch'))">
          <button type="button" class="toggle-password" (click)="togglePasswordVisibility('confirmPassword')">
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M1 12C1 12 5 4 12 4C19 4 23 12 23 12C23 12 19 20 12 20C5 20 1 12 1 12Z" stroke="#34437A" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
              <path d="M12 15C13.6569 15 15 13.6569 15 12C15 10.3431 13.6569 9 12 9C10.3431 9 9 10.3431 9 12C9 13.6569 10.3431 15 12 15Z" stroke="#34437A" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
            </svg>
          </button>
        </div>
      </div>

      <div *ngIf="errorMessage" class="error-message">
        {{ errorMessage }}
      </div>

      <button type="submit" class="login-btn" [disabled]="passwordForm.invalid">
        Reset Password <span class="submit-icon">&#10132;</span>
      </button>
    </form>

    <div *ngIf="isLoading" class="loading-container">
      <div class="spinner"></div>
      <p>Updating password...</p>
    </div>
  </div>
</div>
