import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-splash-screen',
  templateUrl: './splash-screen.component.html',
  styleUrls: ['./splash-screen.component.css']
})
export class SplashScreenComponent implements OnInit {
  isExiting = false;

  constructor(private router: Router) {}

  ngOnInit(): void {
    setTimeout(() => {
      this.isExiting = true;
      // Give time for the exit animation to complete
      setTimeout(() => {
        this.router.navigate(['/sign-up']);
      }, 300);
    }, 2500); // 2.5 seconds
  }
}
<div class="splash-container" [@fadeOut]="isExiting">
  <div class="splash-card">
    <img src="assets/logos/skillmuni-login-logo.png" alt="Skillmuni Logo" class="splash-logo" />

    <div class="welcome-text">
      <h3>Welcome to your hiring dashboard</h3>
      <p>
        — let’s kick off your
        <span class="highlight-container">
          <span class="highlight">recruitment journey.</span>
          <img src="assets/icons/long-arrow.png" alt="arrow" class="arrow-image" />
        </span>
      </p>
    </div>
  </div>
</div>
