import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-company-profile',
  templateUrl: './company-profile.component.html',
  styleUrls: ['./company-profile.component.css']
})
export class CompanyProfileComponent implements OnInit {
  profileForm!: FormGroup;
  logoPreview: string | ArrayBuffer | null = null;

  countryList = ['India', 'United States', 'United Kingdom', 'Australia', 'Canada'];
  cityList = ['Mumbai', 'Delhi', 'Bangalore', 'New York', 'London', 'Sydney'];
  industryList = ['Technology', 'Finance', 'Healthcare', 'Education', 'Retail'];

  formFields: any[] = [
    {
      label: '1. Company <span class="highlight">Name</span>',
      controlName: 'companyName',
      type: 'text',
      placeholder: 'Type in your Company Name here...'
    },
    {
      label: '2. Company <span class="highlight">Email</span>',
      controlName: 'companyEmail',
      type: 'email',
      placeholder: 'Written by Default (Same as Sign Up)'
    },
    {
      label: '3. Company <span class="highlight">Website/LinkedIn Link</span>',
      controlName: 'companyWebsite',
      type: 'text',
      placeholder: 'Drop your website or LinkedIn link here...'
    },
    {
      label: '4. Company <span class="highlight">Logo</span>',
      controlName: 'companyLogo',
      type: 'upload',
      placeholder: 'Click here to upload your company logo'
    },
    {
      label: '5. Head Office <span class="highlight">Country</span>',
      controlName: 'country',
      type: 'select',
      options: this.countryList,
      placeholder: 'Choose Country from the drop-down'
    },
    {
      label: '6. Head Office <span class="highlight">City</span>',
      controlName: 'city',
      type: 'select',
      options: this.cityList,
      placeholder: 'Choose City from the drop-down'
    },
    {
      label: '7. Company’s <span class="highlight">Industry</span>',
      controlName: 'industry',
      type: 'select',
      options: this.industryList,
      placeholder: 'Choose Industry from the drop-down'
    },
    {
      label: '8. Company <span class="highlight">Size</span>',
      controlName: 'size',
      type: 'text',
      placeholder: 'How big is your team? e.g. 11–50 people'
    },
    {
      label: '9. Contact Person <span class="highlight">Name</span>',
      controlName: 'contactName',
      type: 'text',
      placeholder: 'Type in Full name of your point of contact...'
    },
    {
      label: '10. Contact Person <span class="highlight">Email/No</span>',
      controlName: 'contactEmail',
      type: 'text',
      placeholder: 'Type in the best email or number to reach you...'
    }
  ];

  constructor(private fb: FormBuilder) {}

  ngOnInit() {
    this.profileForm = this.fb.group({
      companyName: ['', Validators.required],
      companyEmail: [{ value: '', disabled: true }],
      companyWebsite: ['', Validators.required],
      companyLogo: [null],
      country: ['', Validators.required],
      city: ['', Validators.required],
      industry: ['', Validators.required],
      size: ['', Validators.required],
      contactName: ['', Validators.required],
      contactEmail: ['', [Validators.required, Validators.email]],
      about: ['', [Validators.required, Validators.maxLength(100)]]
    });
  }

  onLogoChange(event: any) {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => (this.logoPreview = reader.result);
      reader.readAsDataURL(file);
      this.profileForm.patchValue({ companyLogo: file });
    }
  }

  onSubmit() {
    if (this.profileForm.valid) {
      alert('Profile submitted!');
    } else {
      this.profileForm.markAllAsTouched();
    }
  }
}
