          <!-- Select Dropdowns -->
          <ng-container *ngIf="field.type === 'select'">
            <select [formControlName]="field.controlName">
              <option value="" disabled selected>{{ field.placeholder }}</option>
              <option *ngFor="let opt of field.options" [value]="opt">{{ opt }}</option>
            </select>
          </ng-container>

          <!-- Logo Upload -->
          <ng-container *ngIf="field.type === 'upload'">
            <label class="upload-btn">
              {{ field.placeholder }}
              <img src="assets/icons/upload-icon.svg" alt="Upload" />
              <input type="file" accept="image/*" (change)="onLogoChange($event)" />
            </label>
            <span *ngIf="logoPreview" class="logo-preview">
              <img [src]="logoPreview" alt="Logo Preview" />
            </span>
          </ng-container>
        </div>
      </div>

      <!-- About Company Section -->
      <div class="profile-about">
        <label>11. About the <span class="highlight">Company</span></label>
        <textarea formControlName="about" maxlength="100" placeholder="Tell us about your company..."></textarea>
        <span class="about-limit">Upto 100 words</span>
      </div>

      <!-- Submit -->
      <div class="save-button-container">
        <button class="save-button" type="submit">
          Let's Go
          <span class="material-icons">arrow_forward</span>
        </button>
      </div>
    </form>
  </div>
</div>
