import { Component } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {
  orgLogoUrl = 'assets/logo.svg';
  userProfileImg = 'assets/profile.svg';
  activeLink: 'home' | 'leaderboard' | 'dashboard' = 'home';
  notifications: string[] = ['Welcome to SkillMuni!', 'You have a new message.'];
  showNotifications = false;

  setActiveLink(link: 'home' | 'leaderboard' | 'dashboard') {
    this.activeLink = link;
  }

  toggleNotifications() {
    this.showNotifications = !this.showNotifications;
  }
}

<header class="skillmuni-header">
  <div class="header-left">
    <img src="assets/logo.svg" alt="SkillMuni Logo" class="header-logo" />
    <span class="header-title">SKILLMUNI<sup>™</sup></span>
  </div>
  <div class="header-right">
    <button class="header-icon-btn" aria-label="Notifications">
      <img src="assets/icons/bell-icon.png" alt="Notifications" class="header-bell" />
    </button>
    <button class="header-icon-btn profile-btn" aria-label="Profile">
      <img src="assets/icons/profile-icon.png" alt="Profile" class="header-profile" />
    </button>
  </div>
</header>
